import React, { useEffect, useState } from 'react';
import api from '../api/api';

export default function VoteParliamentary() {
  const [constituency, setConstituency] = useState('');
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (constituency) {
      const fetchCandidates = async () => {
        try {
          const res = await api.get(`/api/candidates?type=parliamentary&constituency=${constituency}`);
          setCandidates(res.data.candidates || []);
        } catch (err) {
          console.error('Failed to load candidates:', err);
        }
      };
      fetchCandidates();
    }
  }, [constituency]);

  const handleVote = async () => {
    if (!selectedCandidate) return alert('Please select a candidate.');

    try {
      await api.post('/api/votes', {
        candidateId: selectedCandidate,
        electionType: 'parliamentary'
      });
      setMessage('✅ Vote cast successfully!');
    } catch (err) {
      console.error(err);
      setMessage('❌ ' + (err.response?.data?.message || 'Vote failed.'));
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Parliamentary Election</h2>

      <input
        type="text"
        placeholder="Enter your constituency"
        value={constituency}
        onChange={(e) => setConstituency(e.target.value)}
        className="border p-2 rounded mb-4 w-full"
      />

      {candidates.map((c) => (
        <div key={c._id} className="mb-2">
          <label className="flex items-center space-x-2">
            <input
              type="radio"
              name="candidate"
              value={c._id}
              onChange={() => setSelectedCandidate(c._id)}
            />
            <span>{c.name} ({c.party})</span>
          </label>
        </div>
      ))}

      <button
        onClick={handleVote}
        className="bg-green-600 text-white px-4 py-2 rounded mt-4"
      >
        Cast Vote
      </button>

      {message && <p className="mt-4">{message}</p>}
    </div>
  );
}
