import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api'; // Make sure this file is correctly configured
import LoginCard from '../components/LoginCard'; // Ensure this component exists

const AdminLoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/api/admins/login', { email, password });
      const token = res.data.token;

      localStorage.setItem('token', token);
      localStorage.setItem('isAdmin', 'true');

      navigate('/admin-dashboard');
    } catch (err) {
      alert(err.response?.data?.message || 'Admin login failed.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-yellow-100 to-green-200">
      <div className="w-full max-w-md">
        <LoginCard title="Admin Login">
          <form onSubmit={handleLogin} className="space-y-5">
            <div>
              <label className="block mb-1 font-semibold text-green-900">Email</label>
              <input
                type="email"
                placeholder="admin@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="w-full px-3 py-2 rounded border border-green-300 focus:outline-none focus:ring-2 focus:ring-green-400"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold text-green-900">Password</label>
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-3 py-2 rounded border border-green-300 focus:outline-none focus:ring-2 focus:ring-green-400"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-2 rounded shadow transition"
            >
              Login
            </button>
          </form>
        </LoginCard>
        <div className="mt-6 text-center text-gray-500 text-sm">
          &copy; {new Date().getFullYear()} Ghana Electoral Commission. All rights reserved.
        </div>
      </div>
    </div>
  );
};

export default AdminLoginPage;
