// src/pages/ManageCandidates.jsx
import React, { useState } from 'react';
import api from '../api/api';

const ghanaConstituencies = [
  "Ablekuma Central", "Ablekuma North", "Ablekuma South", "Abura/Asebu/Kwamankese",
  "Accra Central", "Adaklu", "Adansi Asokwa", "Adansi North", "Adansi South",
  "Afigya Kwabre North", "Afigya Kwabre South", "Afadzato South", "Agona East",
  "Agona West", "Ahafo Ano North", "Ahafo Ano South East", "Ahafo Ano South West",
  "Ajumako/Enyan/Essiam", "Akatsi North", "Akatsi South", "Akim Abuakwa North",
  "Akim Abuakwa South", "Akim Oda", "Akomadan", "Akwapim North", "Akwapim South",
  "Amansie Central", "Amansie West", "Amenfi Central", "Amenfi East", "Amenfi West",
  "Anlo", "Asene Akroso Manso", "Asokwa", "Asuogyaman", "Asutifi North",
  "Asutifi South", "Atiwa East", "Atiwa West", "Atebubu-Amantin", "Atwima Kwanwoma",
  "Atwima Mponua", "Atwima Nwabiagya North", "Atwima Nwabiagya South", "Avenor-Ave",
  "Ayesuano", "Ayawaso Central", "Ayawaso East", "Ayawaso North", "Ayawaso West Wuogon",
  "Banda", "Bawku Central", "Bawku West", "Bekwai", "Berekum East", "Berekum West",
  "Bia East", "Bia West", "Bibiani-Anhwiaso-Bekwai", "Bimbilla", "Binduri",
  "Birim Central", "Birim North", "Birim South", "Bole", "Bolgatanga Central",
  "Bolgatanga East", "Bongo", "Bosomtwe", "Bosome Freho", "Builsa North",
  "Builsa South", "Central Gonja", "Chereponi", "Daboya/Mankarigu", "Dade-Kotopon",
  "Damongo", "Dormaa Central", "Dormaa East", "Dormaa West", "Dome-Kwabenya",
  "East Gonja", "East Mamprusi", "Effia", "Effutu", "Ejisu", "Ejura-Sekyedumase",
  "Ekumfi", "Ellembelle", "Fanteakwa North", "Fanteakwa South", "Fomena", "Garu",
  "Gomoa Central", "Gomoa East", "Gomoa West", "Ho Central", "Ho West", "Hohoe",
  "Jaman North", "Jaman South", "Jomoro", "Kade", "Kassena Nankana East",
  "Kassena Nankana West", "Keta", "Ketu North", "Ketu South", "Kintampo North",
  "Kintampo South", "Klottey Korle", "Komenda-Edina-Eguafo-Abirem", "Kpandai",
  "Kpone Katamanso", "Krachi East", "Krachi Nchumuru", "Krachi West", "Krowor",
  "Kwabre East", "Kwadaso", "Kwaebibirem", "Kwahu Afram Plains North",
  "Kwahu Afram Plains South", "Kwahu East", "Kwahu South", "La Dadekotopon",
  "Lambussie Karni", "Lawra", "Ledzokuku", "Lower Manya Krobo", "Madina", "Mampong",
  "Manhyia North", "Manhyia South", "Mpraeso", "Mpohor", "Nabdam", "Nadowli/Kaleo",
  "Nandom", "Nanumba North", "Nanumba South", "Nanton", "Nhyiaeso",
  "New Juaben North", "New Juaben South", "Ningo Prampram", "Nkawkaw",
  "North Dayi", "North East Gonja", "North Gonja", "North Tongu", "Nsawam-Adoagyiri",
  "Nzema East", "Obuasi East", "Obuasi West", "Odododiodoo", "Offinso North",
  "Offinso South", "Oforikrom", "Okaikwei Central", "Okaikwei North",
  "Okaikwei South", "Old Tafo", "Pru East", "Pru West", "Saboba", "Salaga North",
  "Salaga South", "Sagnarigu", "Savelugu", "Sawla-Tuna-Kalba", "Sekondi",
  "Sekyere Afram Plains", "Sekyere Central", "Sekyere East", "Sekyere Kumawu",
  "Sekyere South", "Sefwi Akontombra", "Sefwi Wiawso", "Shai-Osudoku",
  "Sissala East", "Sissala West", "Suhum", "Talensi", "Tamale Central", "Tamale North",
  "Tamale South", "Tano North", "Tano South", "Tafo", "Tarkwa-Nsuaem", "Tatale Sanguli",
  "Tolon", "Tongu Central", "Upper Denkyira East", "Upper Denkyira West",
  "Upper Manya Krobo", "Upper West Akim", "Wa Central", "Wa East", "Wa West",
  "Wassa Amenfi Central", "Wassa Amenfi East", "Wassa Amenfi West", "Wassa East",
  "Weija-Gbawe", "Wenchi", "West Gonja", "West Mamprusi", "Yagaba/Kubori", "Yendi",
  "Yilo Krobo", "Yunyoo", "Zabzugu"
];

const ManageCandidates = () => {
  const [name, setName] = useState('');
  const [party, setParty] = useState('');
  const [type, setType] = useState('presidential');
  const [constituency, setConstituency] = useState('');
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState('');

  const handleAddCandidate = async (e) => {
    e.preventDefault();

    if (!image) {
      return setMessage('Please select an image.');
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('party', party);
    formData.append('type', type);
    if (type === 'parliamentary') {
      formData.append('constituency', constituency);
    }
    formData.append('image', image);

    try {
      const token = localStorage.getItem('token');
      const res = await api.post('/candidates', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      setMessage(res.data.message || 'Candidate added successfully ✅');

      // Reset form
      setName('');
      setParty('');
      setType('presidential');
      setConstituency('');
      setImage(null);
      setImagePreview(null);
    } catch (err) {
      console.error('Error adding candidate:', err);
      setMessage(err.response?.data?.message || 'Failed to add candidate ❌');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setImagePreview(file ? URL.createObjectURL(file) : null);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white text-black">
      <div className="w-full max-w-md bg-gray-100 p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Add New Candidate</h2>
        <form onSubmit={handleAddCandidate} className="space-y-4" encType="multipart/form-data">
          <div>
            <label className="block mb-1">Candidate Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter candidate name"
            />
          </div>

          <div>
            <label className="block mb-1">Party</label>
            <input
              type="text"
              value={party}
              onChange={(e) => setParty(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter party name"
            />
          </div>

          <div>
            <label className="block mb-1">Election Type</label>
            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
            >
              <option value="presidential">Presidential</option>
              <option value="parliamentary">Parliamentary</option>
            </select>
          </div>

          {type === 'parliamentary' && (
            <div>
              <label className="block mb-1">Constituency</label>
              <select
                value={constituency}
                onChange={(e) => setConstituency(e.target.value)}
                required
                className="w-full px-3 py-2 rounded border"
              >
                <option value="">-- Select Constituency --</option>
                {ghanaConstituencies.map((c) => (
                  <option key={c} value={c}>{c}</option>
                ))}
              </select>
            </div>
          )}

          <div>
            <label className="block mb-1">Candidate Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              required
              className="w-full"
            />
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Candidate Preview"
                className="mt-2 w-full h-48 object-cover rounded"
              />
            )}
          </div>

          <button
            type="submit"
            className="w-full bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-2 rounded"
          >
            Add Candidate
          </button>
        </form>

        {message && <p className="mt-4 text-center text-sm text-blue-700">{message}</p>}
      </div>
    </div>
  );
};

export default ManageCandidates;
