import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import LoginCard from '../components/LoginCard';

const RegisterPage = () => {
  const [voterId, setVoterId] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await api.post('/voters/register', { voterId, name, password });
      alert('Registered successfully!');
      navigate('/voter-login');
    } catch (err) {
      alert(err.response?.data?.message || 'Registration failed.');
    }
  };

  return (
    <div>
      <LoginCard title="Voter Registration">
        <form onSubmit={handleRegister} className="space-y-4">
          <div>
            <label className="block mb-1">Voter ID</label>
            <input
              type="text"
              placeholder="Voter ID"
              value={voterId}
              onChange={(e) => setVoterId(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <div>
            <label className="block mb-1">Full Name</label>
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <div>
            <label className="block mb-1">Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-2 rounded"
          >
            Register
          </button>
        </form>
      </LoginCard>
    </div>
  );
};

export default RegisterPage;
