import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

export default function VoterDashboard() {
  const [elections, setElections] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchElections = async () => {
      try {
        const res = await api.get('/api/votes/elections', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setElections(Array.isArray(res.data.elections) ? res.data.elections : []);
      } catch (err) {
        console.error('Error fetching elections:', err);
        setError('Failed to load elections');
      }
    };

    fetchElections();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    navigate('/voter/login');
  };

  const handleVoteClick = (type, constituency = '') => {
    if (type === 'presidential') {
      navigate('/vote/presidential');
    } else {
      navigate(`/vote/parliamentary/${constituency}`);
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Welcome, Voter</h2>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      <h3 className="text-lg mb-2">Available Elections:</h3>

      {error && <p className="text-red-600">{error}</p>}

      {elections.length === 0 ? (
        <p>No elections available</p>
      ) : (
        <div className="space-y-4">
          {elections.map((election, i) => (
            <div
              key={i}
              className="border rounded p-4 shadow-sm flex justify-between items-center"
            >
              <div>
                {election.type === 'presidential'
                  ? 'Presidential Election'
                  : `Parliamentary - ${election.constituency}`}
              </div>
              <button
                onClick={() =>
                  handleVoteClick(election.type, election.constituency)
                }
                className="bg-blue-600 text-white px-4 py-2 rounded"
              >
                Vote
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
