import React, { useEffect, useState } from 'react';
import api from '../api/api';

const CandidatesPage = () => {
  const [candidates, setCandidates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCandidates = async () => {
      try {
        const res = await api.get('/candidates'); // Make sure this matches your backend route
        setCandidates(res.data);
      } catch (err) {
        console.error('Error fetching candidates:', err);
        setError('Failed to load candidates');
      } finally {
        setLoading(false);
      }
    };

    fetchCandidates();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6 text-center">Candidates</h1>

      {loading && <p className="text-center text-gray-600">Loading candidates...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}

      {!loading && !error && (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {candidates.map((candidate) => (
            <div
              key={candidate._id}
              className="bg-white rounded-lg shadow-md overflow-hidden"
            >
              {candidate.image && (
                <img
                  src={`http://localhost:3000/uploads/candidates/${candidate.image}`}
                  alt={candidate.name}
                  className="w-full h-48 object-cover"
                />
              )}
              <div className="p-4">
                <h2 className="text-xl font-semibold">{candidate.name}</h2>
                <p className="text-gray-700">Party: {candidate.party}</p>
                <p className="text-gray-600 capitalize">
                  Election Type: {candidate.electionType}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CandidatesPage;
