// src/pages/ManageCandidates.jsx
import React, { useState } from 'react';
import api from '../api/api';
import ghanaConstituencies from '../data/ghanaConstituencies'; // ðŸ” You'll create this file

const ManageCandidates = () => {
  const [name, setName] = useState('');
  const [party, setParty] = useState('');
  const [type, setType] = useState('presidential');
  const [constituency, setConstituency] = useState('');
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState('');

  const handleAddCandidate = async (e) => {
    e.preventDefault();

    if (!image) {
      return setMessage('Please select an image.');
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('party', party);
    formData.append('type', type);
    if (type === 'parliamentary') {
      formData.append('constituency', constituency);
    }
    formData.append('image', image);

    try {
      const token = localStorage.getItem('token');
      const res = await api.post('/candidates', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      setMessage(res.data.message || 'Candidate added successfully âœ…');

      // Reset form
      setName('');
      setParty('');
      setType('presidential');
      setConstituency('');
      setImage(null);
      setImagePreview(null);
    } catch (err) {
      console.error('Error adding candidate:', err);
      setMessage(err.response?.data?.message || 'Failed to add candidate âŒ');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setImagePreview(file ? URL.createObjectURL(file) : null);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white text-black">
      <div className="w-full max-w-md bg-gray-100 p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Add New Candidate</h2>
        <form onSubmit={handleAddCandidate} className="space-y-4" encType="multipart/form-data">
          <div>
            <label className="block mb-1">Candidate Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter candidate name"
            />
          </div>

          <div>
            <label className="block mb-1">Party</label>
            <input
              type="text"
              value={party}
              onChange={(e) => setParty(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter party name"
            />
          </div>

          <div>
            <label className="block mb-1">Election Type</label>
            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              required
              className="w-full px-3 py-2 rounded border"
            >
              <option value="presidential">Presidential</option>
              <option value="parliamentary">Parliamentary</option>
            </select>
          </div>

          {type === 'parliamentary' && (
            <div>
              <label className="block mb-1">Constituency</label>
              <select
                value={constituency}
                onChange={(e) => setConstituency(e.target.value)}
                required
                className="w-full px-3 py-2 rounded border"
              >
                <option value="">Select constituency</option>
                {ghanaConstituencies.map((c) => (
                  <option key={c} value={c}>
                    {c}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div>
            <label className="block mb-1">Candidate Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              required
              className="w-full"
            />
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Candidate Preview"
                className="mt-2 w-full h-48 object-cover rounded"
              />
            )}
          </div>

          <button
            type="submit"
            className="w-full bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-2 rounded"
          >
            Add Candidate
          </button>
        </form>

        {message && <p className="mt-4 text-center text-sm text-blue-700">{message}</p>}
      </div>
    </div>
  );
};

export default ManageCandidates;
