import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import LoginCard from '../components/LoginCard';
import ghanaFlag from '../assets/ghana-flag.png';

const AdminLoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/api/admins/login', { email, password });
      const token = res.data.token;

      localStorage.setItem('token', token);
      localStorage.setItem('isAdmin', 'true');

      navigate('/admin-dashboard');
    } catch (err) {
      alert(err.response?.data?.message || 'Admin login failed.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-yellow-100 to-green-200">
      <div className="w-full max-w-md">
        <div className="flex flex-col items-center mb-6">
          <img src={ghanaFlag} alt="Ghana Flag" className="w-20 h-14 mb-2 rounded shadow" />
          <h1 className="text-2xl font-extrabold text-green-900 mb-1 text-center">
            Ghana National Online Voting
          </h1>
          <p className="text-sm text-gray-700 mb-2 text-center">
            Admin Portal
          </p>
          <div className="flex space-x-2 mb-2">
            <span className="w-6 h-2 bg-red-600 rounded"></span>
            <span className="w-6 h-2 bg-yellow-400 rounded"></span>
            <span className="w-6 h-2 bg-green-700 rounded"></span>
          </div>
        </div>
        <div className="bg-white rounded-2xl shadow-2xl px-8 py-10 border-t-4 border-yellow-400">
          <LoginCard title="Admin Login">
            <form onSubmit={handleLogin} className="space-y-5">
              <div>
                <label className="block mb-1 font-semibold text-green-900">Email</label>
                <input
                  type="email"
                  placeholder="admin@example.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  className="w-full px-3 py-2 rounded border border-green-300 focus:outline-none focus:ring-2 focus:ring-green-400"
                />
              </div>
              <div>
                <label className="block mb-1 font-semibold text-green-900">Password</label>
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  className="w-full px-3 py-2 rounded border border-green-300 focus:outline-none focus:ring-2 focus:ring-green-400"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-gradient-to-r from-red-600 via-yellow-400 to-green-700 hover:from-green-700 hover:to-red-600 text-white font-bold py-2 rounded shadow transition"
              >
                Login
              </button>
            </form>
          </LoginCard>
        </div>
        <div className="mt-6 text-center text-gray-500 text-sm">
          &copy; {new Date().getFullYear()} Ghana Electoral Commission. All rights reserved.
        </div>
      </div>
    </div>
  );
};

export default AdminLoginPage;
