import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [admins, setAdmins] = useState([]);
  const [form, setForm] = useState({
    email: '',
    password: '',
  });

  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    navigate('/admin-login');
  };

  const fetchAdmins = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/admins', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAdmins(res.data); // ✅ FIXED: directly set the array
    } catch (err) {
      console.error('Error fetching admins:', err);
      setAdmins([]); // fallback to prevent crashing
    }
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/admins', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setForm({ email: '', password: '' });
      fetchAdmins();
    } catch (err) {
      console.error('Error adding admin:', err);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Admin Dashboard</h2>

      <div className="space-x-3 mb-8">
        <button onClick={() => navigate('/candidates')} className="bg-blue-600 text-white px-4 py-2 rounded">
          Manage Candidates
        </button>
        <button onClick={() => navigate('/results')} className="bg-green-600 text-white px-4 py-2 rounded">
          View Results
        </button>
        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded">
          Logout
        </button>
      </div>

      <div className="border-t pt-6">
        <h3 className="text-xl font-semibold mb-4">Add New Admin</h3>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleInputChange}
            placeholder="Admin Email"
            className="border p-2"
            required
          />
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleInputChange}
            placeholder="Admin Password"
            className="border p-2"
            required
          />
          <button type="submit" className="bg-blue-700 text-white px-4 py-2 rounded col-span-2">
            Add Admin
          </button>
        </form>

        <h3 className="text-lg font-semibold mb-2">Current Admins</h3>
        <ul className="space-y-2">
          {Array.isArray(admins) && admins.map((admin) => (
            <li key={admin._id} className="border p-3 rounded bg-gray-50">
              <strong>{admin.email}</strong> ({admin.role})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
