import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

const VoterLoginPage = () => {
  const [voterId, setVoterId] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/api/voters/login', { voterId, password });
      const token = res.data.token;

      localStorage.setItem('token', token);
      localStorage.setItem('isAdmin', 'false');

      navigate('/voter-dashboard');
    } catch (err) {
      alert(err.response?.data?.message || 'Voter login failed.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-white to-indigo-100">
      <div className="w-full max-w-md">
        <div className="bg-white rounded-3xl shadow-2xl px-10 py-12 relative">
          <div className="flex flex-col items-center mb-6">
            <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center shadow mb-2">
              <svg width="36" height="36" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="text-blue-500">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
            </div>
            <h2 className="text-2xl font-extrabold text-center text-gray-800 mb-1">Voter Login</h2>
            <p className="text-gray-500 text-sm mb-2">Access your secure voting dashboard</p>
          </div>
          <form onSubmit={handleLogin} className="space-y-5">
            <div>
              <label className="block mb-1 font-semibold text-gray-700">Voter ID</label>
              <input
                type="text"
                placeholder="Enter your Voter ID"
                value={voterId}
                onChange={(e) => setVoterId(e.target.value)}
                required
                className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-400 focus:outline-none transition"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold text-gray-700">Password</label>
              <input
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-400 focus:outline-none transition"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-indigo-700 hover:to-blue-700 text-white font-bold py-2 rounded-lg shadow transition"
            >
              Login
            </button>
          </form>
        </div>
        <div className="mt-6 text-center text-gray-500 text-sm">
          &copy; {new Date().getFullYear()} National Online Voting System. All rights reserved.
        </div>
      </div>
    </div>
  );
};

export default VoterLoginPage;
