import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

export default function VoterDashboard() {
  const [elections, setElections] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchElections = async () => {
      try {
        const res = await api.get('/api/votes/elections', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setElections(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.error('Error fetching elections:', err);
      }
    };
    fetchElections();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    navigate('/voter-login');
  };

  const handleVoteClick = (type, constituency = '') => {
    // Route to the vote page, optionally include constituency for parliamentary elections
    if (type === 'presidential') {
      navigate('/vote/presidential');
    } else {
      navigate(`/vote/parliamentary/${constituency}`);
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Welcome, Voter</h2>
        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded">
          Logout
        </button>
      </div>
      <h3 className="text-lg mb-2">Available Elections:</h3>
      {elections.length === 0 ? (
        <p>No elections available</p>
      ) : (
        elections.map((e, idx) => (
          <div key={idx} className="mb-4 border p-4 rounded shadow">
            <div className="font-semibold">
              {e.type === 'presidential'
                ? 'Presidential Election'
                : `Parliamentary - ${e.constituency}`}
            </div>
            <button
              onClick={() => handleVoteClick(e.type, e.constituency)}
              className="mt-2 bg-blue-600 text-white px-4 py-2 rounded"
            >
              Vote
            </button>
          </div>
        ))
      )}
    </div>
  );
}
