import React, { useEffect, useState } from 'react';
import api from '../api/api';

const ManageCandidates = () => {
  const [candidates, setCandidates] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    party: '',
    type: '',
    image: null,
  });

  const fetchCandidates = async () => {
    try {
      const res = await api.get('/candidates');
      setCandidates(res.data.candidates);
    } catch (err) {
      alert('Failed to fetch candidates');
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      data.append('name', formData.name);
      data.append('party', formData.party);
      data.append('type', formData.type);
      if (formData.image) data.append('image', formData.image);

      await api.post('/candidates', data);
      alert('Candidate added!');
      setFormData({ name: '', party: '', type: '', image: null });
      fetchCandidates();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to add candidate');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Manage Candidates</h2>

      <form onSubmit={handleSubmit} className="space-y-4 mb-6">
        <input
          type="text"
          name="name"
          value={formData.name}
          placeholder="Name"
          onChange={handleChange}
          required
          className="block w-full px-4 py-2 border rounded"
        />
        <input
          type="text"
          name="party"
          value={formData.party}
          placeholder="Party"
          onChange={handleChange}
          required
          className="block w-full px-4 py-2 border rounded"
        />
        <input
          type="text"
          name="type"
          value={formData.type}
          placeholder="Position (e.g. President)"
          onChange={handleChange}
          required
          className="block w-full px-4 py-2 border rounded"
        />
        <input
          type="file"
          name="image"
          onChange={handleChange}
          className="block w-full"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Add Candidate
        </button>
      </form>

      <div>
        <h3 className="text-xl font-semibold mb-2">Current Candidates</h3>
        <ul>
          {candidates.map((c) => (
            <li key={c._id} className="mb-2">
              <strong>{c.name}</strong> ({c.party}) - {c.type}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ManageCandidates;
