import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [admins, setAdmins] = useState([]);
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
  });

  const token = localStorage.getItem('token');

  const fetchAdmins = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/admins', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAdmins(res.data.admins);
    } catch (err) {
      console.error('Error fetching admins:', err);
    }
  };

  useEffect(() => {
    fetchAdmins();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/admins', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setForm({ name: '', email: '', password: '' });
      fetchAdmins();
    } catch (err) {
      console.error('Error adding admin:', err);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">Add New Admin</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleInputChange}
          placeholder="Full Name"
          className="border p-2"
          required
        />
        <input
          type="email"
          name="email"
          value={form.email}
          onChange={handleInputChange}
          placeholder="Email"
          className="border p-2"
          required
        />
        <input
          type="password"
          name="password"
          value={form.password}
          onChange={handleInputChange}
          placeholder="Password"
          className="border p-2"
          required
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded col-span-2">
          Add Admin
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">Current Admins</h3>
      <ul className="space-y-2">
        {admins.map((admin) => (
          <li key={admin._id} className="border p-2 rounded">
            <strong>{admin.name}</strong> â€” {admin.email} ({admin.role})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
