import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';

export default function VoterDashboard() {
  const [elections, setElections] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchElections = async () => {
      try {
        const res = await api.get('/api/votes/elections'); // ✅ Make sure this route exists in backend
        setElections(Array.isArray(res.data.elections) ? res.data.elections : []);
      } catch (err) {
        console.error('Error fetching elections:', err);
        setError('Failed to load elections');
      }
    };

    fetchElections();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    navigate('/voter-login');
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Welcome, Voter</h2>
        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded">
          Logout
        </button>
      </div>

      <h3 className="text-lg mb-2">Available Elections:</h3>

      {error && <p className="text-red-600">{error}</p>}

      {elections.length === 0 ? (
        <p>No elections available</p>
      ) : (
        <ul className="list-disc ml-6">
          {elections.map((election, i) => (
            <li key={i}>
              {election.type === 'presidential'
                ? 'Presidential Election'
                : `Parliamentary - ${election.constituency}`}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
  {elections.map((e, idx) => (
  <div key={idx} className="mb-4">
    <div>
      {e.type === 'presidential'
        ? 'Presidential Election'
        : `Parliamentary - ${e.constituency}`}
    </div>
    <button
      onClick={() => navigate(`/vote/${e.type}${e.constituency ? `/${e.constituency}` : ''}`)}
      className="mt-2 bg-blue-600 text-white px-4 py-2 rounded"
    >
      Vote
    </button>
  </div>
))}

}
