import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api'; // Make sure this file is correctly configured
import LoginCard from '../components/LoginCard'; // Ensure this component exists

const AdminLoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log('Attempting admin login with:', email);
      const res = await api.post('/api/admins/login', { email, password }); // ✅ Corrected route
      const token = res.data.token;

      localStorage.setItem('token', token);
      localStorage.setItem('isAdmin', 'true');

      console.log('✅ Admin logged in');
      navigate('/admin-dashboard');
    } catch (err) {
      console.error('❌ Login error:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Admin login failed.');
    }
  };

  return (
    <div>
      <LoginCard title="Admin Login">
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block mb-1">Email</label>
            <input
              type="email"
              placeholder="admin@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <div>
            <label className="block mb-1">Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-2 rounded"
          >
            Login
          </button>
        </form>
      </LoginCard>
    </div>
  );
};

export default AdminLoginPage;
