import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CandidatesPage = () => {
  const [candidates, setCandidates] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [partyFilter, setPartyFilter] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [itemsPerPage] = useState(5);

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchCandidates();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [partyFilter, typeFilter, candidates]);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/candidates');
      setCandidates(res.data.candidates);
      setFiltered(res.data.candidates);
    } catch (err) {
      console.error('Error fetching candidates:', err);
    }
  };

  const applyFilters = () => {
    let temp = [...candidates];
    if (partyFilter) {
      temp = temp.filter(c => c.party === partyFilter);
    }
    if (typeFilter) {
      temp = temp.filter(c => c.type === typeFilter);
    }
    setFiltered(temp);
    setCurrentPage(1); // reset to first page on filter change
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this candidate?')) return;
    try {
      await axios.delete(`http://localhost:3000/api/candidates/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchCandidates();
    } catch (err) {
      console.error('Error deleting candidate:', err);
    }
  };

  const totalPages = Math.ceil(filtered.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedCandidates = filtered.slice(startIndex, startIndex + itemsPerPage);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Candidates</h2>

      <div style={{ marginBottom: '15px' }}>
        <select value={partyFilter} onChange={(e) => setPartyFilter(e.target.value)}>
          <option value="">All Parties</option>
          {[...new Set(candidates.map(c => c.party))].map(party => (
            <option key={party} value={party}>{party}</option>
          ))}
        </select>

        <select value={typeFilter} onChange={(e) => setTypeFilter(e.target.value)} style={{ marginLeft: '10px' }}>
          <option value="">All Types</option>
          <option value="presidential">Presidential</option>
          <option value="parliamentary">Parliamentary</option>
        </select>
      </div>

      {paginatedCandidates.length === 0 ? (
        <p>No candidates found.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Party</th>
              <th>Type</th>
              <th>Constituency</th>
              <th>Image</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {paginatedCandidates.map(candidate => (
              <tr key={candidate._id}>
                <td>{candidate.name}</td>
                <td>{candidate.party}</td>
                <td>{candidate.type}</td>
                <td>{candidate.constituency}</td>
                <td>
                  {candidate.image ? (
                    <img
                      src={`http://localhost:3000/uploads/${candidate.image}`}
                      alt="candidate"
                      width="60"
                    />
                  ) : 'No image'}
                </td>
                <td>
                  {/* Add your edit modal/component here */}
                  <button onClick={() => handleDelete(candidate._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Pagination controls */}
      <div style={{ marginTop: '20px' }}>
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => setCurrentPage(i + 1)}
            disabled={currentPage === i + 1}
            style={{ marginRight: '5px' }}
          >
            {i + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CandidatesPage;
