import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CandidatesPage = () => {
  const [candidates, setCandidates] = useState([]);
  const [filteredCandidates, setFilteredCandidates] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [typeFilter, setTypeFilter] = useState('');
  const [form, setForm] = useState({
    name: '',
    party: '',
    type: 'presidential',
    constituency: '',
    image: null
  });

  const token = localStorage.getItem('token');

  const fetchCandidates = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/candidates');
      setCandidates(res.data.candidates);
      setFilteredCandidates(res.data.candidates);
    } catch (err) {
      console.error('Error fetching candidates:', err);
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  useEffect(() => {
    if (typeFilter === '') {
      setFilteredCandidates(candidates);
    } else {
      const filtered = candidates.filter(c => c.type === typeFilter);
      setFilteredCandidates(filtered);
    }
  }, [typeFilter, candidates]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = e => {
    setForm(prev => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('party', form.party);
    formData.append('type', form.type);
    if (form.type === 'parliamentary') {
      formData.append('constituency', form.constituency);
    }
    if (form.image) {
      formData.append('image', form.image);
    }

    try {
      if (editingId) {
        await axios.put(`http://localhost:3000/api/candidates/${editingId}`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        });
        setEditingId(null);
      } else {
        await axios.post('http://localhost:3000/api/candidates', formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        });
      }
      setForm({ name: '', party: '', type: 'presidential', constituency: '', image: null });
      fetchCandidates();
    } catch (err) {
      console.error('Error saving candidate:', err);
    }
  };

  const handleDelete = async id => {
    if (!window.confirm('Are you sure you want to delete this candidate?')) return;
    try {
      await axios.delete(`http://localhost:3000/api/candidates/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCandidates();
    } catch (err) {
      console.error('Error deleting candidate:', err);
    }
  };

  const handleEdit = candidate => {
    setEditingId(candidate._id);
    setForm({
      name: candidate.name,
      party: candidate.party,
      type: candidate.type,
      constituency: candidate.constituency || '',
      image: null
    });
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">{editingId ? 'Edit Candidate' : 'Add Candidate'}</h2>
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <input type="text" name="name" value={form.name} onChange={handleInputChange} placeholder="Name" className="border p-2" required />
        <input type="text" name="party" value={form.party} onChange={handleInputChange} placeholder="Party" className="border p-2" required />
        <select name="type" value={form.type} onChange={handleInputChange} className="border p-2">
          <option value="presidential">Presidential</option>
          <option value="parliamentary">Parliamentary</option>
        </select>
        {form.type === 'parliamentary' && (
          <input type="text" name="constituency" value={form.constituency} onChange={handleInputChange} placeholder="Constituency" className="border p-2" required />
        )}
        <input type="file" onChange={handleImageChange} className="border p-2 md:col-span-2" />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded col-span-2">
          {editingId ? 'Update' : 'Add'} Candidate
        </button>
      </form>

      <div className="mb-4">
        <label className="mr-2 font-semibold">Filter by Election Type:</label>
        <select value={typeFilter} onChange={e => setTypeFilter(e.target.value)} className="border p-2">
          <option value="">All</option>
          <option value="presidential">Presidential</option>
          <option value="parliamentary">Parliamentary</option>
        </select>
      </div>

      <h2 className="text-xl font-semibold mb-2">Candidates</h2>
      {filteredCandidates.length === 0 ? (
        <p>No candidates found.</p>
      ) : (
        <div className="space-y-4">
          {filteredCandidates.map(candidate => (
            <div key={candidate._id} className="border p-4 rounded shadow-sm flex items-center justify-between">
              <div>
                <p><strong>{candidate.name}</strong> — {candidate.party} ({candidate.type})</p>
                {candidate.constituency && <p>Constituency: {candidate.constituency}</p>}
                {candidate.image && (
                  <img
                    src={`http://localhost:3000/uploads/${candidate.image}`}
                    alt={candidate.name}
                    className="w-20 h-20 mt-2 object-cover border rounded"
                  />
                )}
              </div>
              <div className="flex gap-2">
                <button onClick={() => handleEdit(candidate)} className="bg-yellow-400 px-3 py-1 text-white rounded">Edit</button>
                <button onClick={() => handleDelete(candidate._id)} className="bg-red-600 px-3 py-1 text-white rounded">Delete</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CandidatesPage;
