// src/pages/VoterLoginPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/api';
import LoginCard from '../components/LoginCard';

const VoterLoginPage = () => {
  const [voterId, setVoterId] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log('Attempting voter login with:', voterId);
      const res = await api.post('/api/voters/login', { voterId, password }); // ✅ corrected path
      const token = res.data.token;

      localStorage.setItem('token', token);
      localStorage.setItem('isAdmin', 'false');

      console.log('✅ Voter logged in');
      navigate('/voter-dashboard');
    } catch (err) {
      console.error('❌ Voter login error:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Voter login failed.');
    }
  };

  return (
    <div>
      <LoginCard title="Voter Login">
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block mb-1">Voter ID</label>
            <input
              type="text"
              placeholder="Voter ID"
              value={voterId}
              onChange={(e) => setVoterId(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <div>
            <label className="block mb-1">Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-3 py-2 rounded bg-white bg-opacity-20 text-black"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-emerald-600 hover:bg-emerald-700 text-white font-medium py-2 rounded"
          >
            Login
          </button>
        </form>
      </LoginCard>
    </div>
  );
};

export default VoterLoginPage;
