import React, { useEffect, useState } from 'react';
import api from '../api/api';

export default function VoteParliamentary() {
  const [constituency, setConstituency] = useState('');
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (constituency) {
      const fetchCandidates = async () => {
        try {
          const res = await api.get(
            `/api/candidates?type=parliamentary&constituency=${constituency}`
          );
          setCandidates(res.data.candidates || []);
        } catch (err) {
          console.error('Failed to load candidates:', err);
          setMessage('❌ Failed to load candidates.');
        }
      };
      fetchCandidates();
    } else {
      setCandidates([]);
      setSelectedCandidate('');
    }
  }, [constituency]);

  const handleVote = async () => {
    if (!selectedCandidate) {
      alert('Please select a candidate.');
      return;
    }

    setLoading(true);
    setMessage('');
    try {
      await api.post('/api/votes', {
        candidateId: selectedCandidate,
        electionType: 'parliamentary'
        // ✅ No need to send constituency — backend gets it from candidate
      });

      setMessage('✅ Vote cast successfully!');
    } catch (err) {
      console.error(err);
      setMessage('❌ ' + (err.response?.data?.message || 'Vote failed.'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Parliamentary Election</h2>

      <input
        type="text"
        placeholder="Enter your constituency"
        value={constituency}
        onChange={(e) => setConstituency(e.target.value.toUpperCase())}
        className="border p-2 rounded mb-6 w-full"
      />

      {constituency && candidates.length === 0 && (
        <p className="text-gray-600 mb-4">
          ⚠️ No candidates found for this constituency.
        </p>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {candidates.map((c) => (
          <div
            key={c._id}
            className={`border p-4 rounded shadow ${
              selectedCandidate === c._id ? 'border-green-500' : ''
            }`}
          >
            <img
              src={`http://localhost:3000${c.image}`} // Make sure backend serves static images
              alt={c.name}
              className="w-full h-48 object-cover rounded mb-3"
            />
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="candidate"
                value={c._id}
                checked={selectedCandidate === c._id}
                onChange={() => setSelectedCandidate(c._id)}
              />
              <span className="font-medium">
                {c.name} ({c.party})
              </span>
            </label>
          </div>
        ))}
      </div>

      <button
        onClick={handleVote}
        disabled={!selectedCandidate || loading}
        className="bg-green-600 text-white px-4 py-2 rounded mt-6 disabled:opacity-50"
      >
        {loading ? 'Submitting Vote...' : 'Cast Vote'}
      </button>

      {message && <p className="mt-4 text-lg">{message}</p>}
    </div>
  );
}
