import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CandidatesPage = () => {
  const [candidates, setCandidates] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    party: '',
    type: 'presidential',
    constituency: '',
    image: null,
  });

  const fetchCandidates = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/candidates');
      setCandidates(res.data.candidates || []);
    } catch (err) {
      console.error('Error fetching candidates:', err);
    }
  };

  useEffect(() => {
    fetchCandidates();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();

    data.append('name', formData.name);
    data.append('party', formData.party);
    data.append('type', formData.type);
    if (formData.type === 'parliamentary') {
      data.append('constituency', formData.constituency);
    }
    if (formData.image) {
      data.append('image', formData.image);
    }

    try {
      const token = localStorage.getItem('token'); // Assume token is stored here
      await axios.post('http://localhost:3000/api/candidates', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Candidate added successfully!');
      setFormData({
        name: '',
        party: '',
        type: 'presidential',
        constituency: '',
        image: null,
      });
      fetchCandidates();
    } catch (err) {
      console.error('Error adding candidate:', err);
      alert('Failed to add candidate');
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Manage Candidates</h2>

      <form onSubmit={handleSubmit} className="mb-8 space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Candidate Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full border p-2"
          required
        />

        <input
          type="text"
          name="party"
          placeholder="Party"
          value={formData.party}
          onChange={handleChange}
          className="w-full border p-2"
          required
        />

        <select
          name="type"
          value={formData.type}
          onChange={handleChange}
          className="w-full border p-2"
        >
          <option value="presidential">Presidential</option>
          <option value="parliamentary">Parliamentary</option>
        </select>

        {formData.type === 'parliamentary' && (
          <input
            type="text"
            name="constituency"
            placeholder="Constituency"
            value={formData.constituency}
            onChange={handleChange}
            className="w-full border p-2"
            required
          />
        )}

        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
          className="w-full border p-2"
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Add Candidate
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">All Candidates</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {Array.isArray(candidates) &&
          candidates.map((candidate) => (
            <div key={candidate._id} className="border p-4 rounded">
              <h4 className="font-bold">{candidate.name}</h4>
              <p>Party: {candidate.party}</p>
              <p>Type: {candidate.type}</p>
              {candidate.constituency && <p>Constituency: {candidate.constituency}</p>}
              {candidate.image && (
                <img
                  src={`http://localhost:3000/uploads/${candidate.image}`}
                  alt={candidate.name}
                  className="w-32 h-32 object-cover mt-2"
                />
              )}
            </div>
          ))}
      </div>
    </div>
  );
};

export default CandidatesPage;
